            <Row className='bg-yellow'>
                <Col><div>ABV: 5.5%</div></Col>
                <Col><div>SRM: 8.5%</div></Col>
                <Col><div>IBC: 17</div></Col>
                <Col><div>IBU: 17</div></Col>
                <Col><div>PH: 4.4</div></Col>
            </Row>
        </Container>
    )
}

export default SingleBeerScreen;
