import { useState, useContext, createContext } from "react";
import useFetch from "./useFetch";

const AppContext = createContext();

const AppProvider = ({ children }) => {
    const [queryPage, setQueryPage] = useState(1);
    const [queryBeerName, setQueryBeerName] = useState('');
    const [queryMinABV, setQueryMinABV] = useState(0);
    const [queryMaxABV, setQueryMaxABV] = useState(15);
    const [type, setType] = useState(false);
    const { isLoading, data, isError, count} = useFetch(`${queryPage}`, `${queryBeerName}`, `${queryMinABV}`, `${queryMaxABV}`);

    const searchBeer = (input, inputMin, inputMax) => {
        setQueryBeerName (input);
        setQueryMinABV (inputMin);
        setQueryMaxABV (inputMax);
    }

    return <AppContext.Provider value={
        {
            type,
            setType,
            queryPage,
            setQueryPage,
            isLoading,
            data,
            isError,
            count,
            searchBeer,
        }}>
        {children}
    </AppContext.Provider>
}

const useGlobalContext = () => {
    return useContext(AppContext)
}

export { AppProvider, useGlobalContext };
