import { useState, useContext, createContext } from "react";
import useFetch from "./useFetch";

const AppContext = createContext();

const AppProvider = ({ children }) => {
    const [query, setQuery] = useState(1);
    const [typeOfSearch, setTypeOfSearch] = useState(0);
    const [inputMinABV, setInputMinABV] = useState(0);
    const [inputMaxABV, setInputMaxABV] = useState(15);
    const [type, setType] = useState(false);
    const { isLoading, data, isError, count } = useFetch(`${query}`, type);

    const searchBeer = (input) => {
        setQuery(`page=${inputPage}${typeOfSearch}${input}&abv_gt=${inputMinABV}}&abv_lt=${inputMaxABV}`);
    }

    return <AppContext.Provider value={
        {
            type,
            setType,
            queryPage,
            setQueryPage,
            queryBeerName,
            setQueryBeerName,
            typeOfSearch,
            setTypeOfSearch,
            //setQueryMinABV,
            //queryMaABV,
            //setQueryMaxABV,
            isLoading,
            data,
            isError,
            count,
            searchBeer,
        }}>
        {children}
    </AppContext.Provider>
}

const useGlobalContext = () => {
    return useContext(AppContext)
}

export { AppProvider, useGlobalContext };
