import { useState, useEffect } from "react";
import { useGlobalContext } from "./context";
import axios from 'axios';


const useFetch = (page, beerName) => {
    const baseUrl = `https://api.punkapi.com/v2/beers?page=${page}`;
    const { typeOfSearch } = useGlobalContext();
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [isError, setIsError] = useState(false);

    let url = baseUrl;

    !beerName
        ? url = baseUrl
        : typeOfSearch === 0
            ? url = `${baseUrl}&beer_name=${beerName}`
            : url = `${baseUrl}&food=${beerName}`

    console.log(url)

    useEffect(() => {
        (async () => {
            setIsError(false);
            setIsLoading(false);
            try {
                const response = await axios.get(url);
                //   const totalItems = response.headers;
                setData(response.data);
                console.log(response.data);
            } catch (err) {
                setIsError(false);
            }
        })(url, page, beerName)
    }, [url, page, beerName]);
    return { isLoading, data, isError };

}

export default useFetch;
