import { useState } from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { useGlobalContext } from './context';
import SearchIcon from '@mui/icons-material/Search';

const FilterForm = () => {

    const {
        searchBeer,
        setInput,
        input,
        // setInputMin,
        // setInputMax,
        setTypeOfSearch,
    } = useGlobalContext();



    const handleSubmit = (e) => {
        e.preventDefault();
        searchBeer(input);
    }

    const handleSearch = (e) => {
        e.target.value == 0 ? setTypeOfSearch('&beer_name=') : setTypeOfSearch('&food=');
        setInput('');
        searchBeer('');


    }

    return (
        <Container>
            <form onSubmit={handleSubmit}>
                <Row>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='sm-input' type="text" placeholder="Search for" name="filterBy" value={input} autoComplete='off' onChange={(e) => setInput(e.target.value)} />
                        <Form.Select aria-label="Default select example" onChange={(e) => handleSearch(e)} >
                            <option value="0">Beer Name</option>
                            <option value="1">Food</option>
                        </Form.Select>
                        <Button className='searchButton' type='submit'> <SearchIcon /> </Button>
                    </Col>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <div className='d-flex justify-content-center'>
                            <div className='button-PercentageOfAlcohol mx-2'>
                                All
                            </div>
                            <div className='button-PercentageOfAlcohol mx-2'>
                                0% - 5%
                            </div>
                            <div className='button-PercentageOfAlcohol mx-2'>
                                5% - 8%
                            </div>
                            <div className='button-PercentageOfAlcohol mx-2'>
                                8% +
                            </div>
                        </div>
                    </Col>
                </Row>
            </form>
        </Container>
    );
};

export default FilterForm;
