import { useState, useEffect } from "react";
import axios from 'axios';
import { useGlobalContext } from "./context";

const searchUrl = 'https://api.punkapi.com/v2/beers';
const singleUrl = 'https://api.punkapi.com/v2/beers';
const { type } = useGlobalContext(AppContext);

const useFetch = (query, type) => {
    const url = type ? singleUrl : searchUrl;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] =useState([]);
    const [count, setCount] = useState(0);
    const [ isError, setIsError ] = useState(false);

    useEffect(()=>{
        (async(query)=>{
            setIsError(false);
            setIsLoading(false);
            try{
                const response = await axios.get(`${url}${query}`);
                setData(response.data);
                setCount(response.data.beers);
                console.log(response.data);
            } catch (err) {
                setIsError(false);
                setCount(0);
            }
        })(query)
    }, [url, query]);
    return{ isLoading, data, isError, count };

}

export default useFetch;
