import { useEffect, useState } from "react";
import { useGlobalContext } from '../context';
import { ListGroup, } from 'react-bootstrap';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';

const ItemBeer = ({ el }) => {

    const {
        favouritesArray,
        setFavouritesArray,
    } = useGlobalContext();

    const [favourites, setFavourites] = useState(localStorage.getItem(beerName) === 'true' ? true : false);
    const [starIcon, setStarIcon] = useState(<StarBorderIcon />);

    useEffect(() => {
        if (favourites) {
            setStarIcon(<StarIcon sx={{ fontSize: 30 }} />)
        } else {
            setStarIcon(<StarBorderIcon sx={{ fontSize: 30 }} />)
        }
    }, [favourites])

    const saveFavourites = () => {
        //save favourite on localstorage
        localStorage.setItem(el.name, !favourites);
        setFavourites(!favourites);
        setFavouritesArray([1]);
        // let backupArray = [...favouritesArray];
        // console.log(backupArray.findIndex(item => item.id === el.id));
        // backupArray.push({ id: el.id, name: el.name, isFavourite: favourites });
        // setFavouritesArray(backupArray);
    }
    return (
        <ListGroup.Item as="li" className='d-flex justify-content-between'><div>{el.name}</div>
            <div className='d-flex'>
                <div className='px-5'>{el.abv}%</div>
                <div className='starIcon' onClick={() => saveFavourites()}>{starIcon}</div>
            </div>
        </ListGroup.Item>
    )
}

export default ItemBeer;
