import { useState } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { useGlobalContext } from '../context';

const FilterForm = () => {

    const {
        searchBeer,
        setType,
    } = useGlobalContext();
    const [input, setInput] = useState('_');
    const [inputMin, setInputMin] = useState('1');
    const [inputMax, setInputMax] = useState('15');

    const handleSubmit = (e) => {
        e.preventDefault();
        searchBeer(input, inputMin, inputMax);
    }

    return (
        <Container>
            <form onSubmit={handleSubmit}>
                <Row>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='lg-input' type="text" placeholder="Filter by name or food" name="filterBy" autoComplete='off' onChange={(e) => setInput(e.target.value)} />
                    </Col>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='sm-input' type="number" placeholder="DA" name="numberOfPartecipants" id="numberOfPartecipants" onChange={(e) => setInputMin(e.target.value)} />
                        <input className='sm-input' type="number" placeholder="A" name="numberOfPartecipants" id="numberOfPartecipants" onChange={(e) => setInputMax(e.target.value)} />
                    </Col>
                </Row>
                <Button type='submit' >Submit</Button>
            </form>
        </Container>
    );
};

export default FilterForm;
