import { useState, useContext, createContext, useEffect } from "react";
import useFetch from "./useFetch";

const AppContext = createContext();

const AppProvider = ({ children }) => {

    const [query, setQuery] = useState('');
    const [type, setType] = useState('true');
    const [queryHome, setQueryHome] = useState('');
    const [querySingleBeer, setQuerySingleBeer] = useState('');
    const [inputPage, setInputPage] = useState(1);
    const [favouritesArray, setFavouritesArray] = useState([]);

    const { isLoading, data, isError, count } = useFetch(`${query}`, `${type}`);

    useEffect(() => { setQuery(queryHome) }, [queryHome]);
    useEffect(() => { setQuery(querySingleBeer) }, [querySingleBeer]);

    return <AppContext.Provider value={
        {
            type,
            setType,
            inputPage,
            setInputPage,
            query,
            queryHome,
            setQueryHome,
            querySingleBeer,
            setQuerySingleBeer,
            isLoading,
            data,
            isError,
            count,
            favouritesArray,
            setFavouritesArray,

        }}>
        {children}
    </AppContext.Provider>
}

const useGlobalContext = () => {
    return useContext(AppContext)
}

export { AppProvider, useGlobalContext };
