import { useState } from "react";
import { ListGroup, } from 'react-bootstrap';
import AddFavouritesButton from './AddFavouritesButton';

const ItemBeer = ({ el, id }) => {

    const beerName = el.name;

    const [favourites, setFavourites] = useState(localStorage.getItem(beerName));

    const saveLocalstorage = () => {
        localStorage.setItem(beerName, setFavourites(""));
    }
    return (
        <ListGroup as="ul" key={id}>
            <ListGroup.Item as="li" className='d-flex justify-content-between'><div>{beerName}</div>
                <div onClick={() => saveLocalstorage()}><AddFavouritesButton favourites={favourites} /></div>
            </ListGroup.Item>
        </ListGroup>
    )
}

export default ItemBeer;
