import { useState, useEffect } from "react";
import axios from 'axios';

const useFetch = (page, beerName) => {
    const allBeerUrl = `https://api.punkapi.com/v2/beers?page=${page}&per_page=80`;
    const beerNameUrl = `https://api.punkapi.com/v2/beers?page=${page}&per_page=80&beer_name=${beerName}`;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [isError, setIsError] = useState(false);

    const url = beerName ? beerNameUrl : allBeerUrl;

    useEffect(() => {
        (async () => {
            setIsError(false);
            setIsLoading(false);
            try {
                const response = await axios.get(url);
                const totalItems = response.headers;
                setData(response.data);
            } catch (err) {
                setIsError(false);
            }
        })(url, page, beerName)
    }, [url, page, beerName]);
    return { isLoading, data, isError };

}

export default useFetch;
