import { useState, useEffect } from "react";
import axios from 'axios';

const searchUrl = 'https://api.punkapi.com/v2/beers?page=';
const singleUrl = 'https://api.punkapi.com/v2/beers?page=';


const useFetch = (page, beerName, type) => {
    const url = type ? singleUrl : searchUrl;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] =useState([]);
    const [ isError, setIsError ] = useState(false);

    useEffect(()=>{
        (async()=>{
            setIsError(false);
            setIsLoading(false);
            try{
                const response = await axios.get(`${url}${page}&food=${beerName}`);
                setData(response.data);
            } catch (err) {
                setIsError(false);
            }
        })(page, beerName)
    }, [page, beerName]);
    return{ isLoading, data, isError };

}

export default useFetch;
