import { useState, useEffect } from "react";
import axios from 'axios';
import { useGlobalContext } from "./context";


const useFetch = (query, type) => {
    const searchUrl = `https://api.punkapi.com/v2/beers?page=${inputPage}&abv_gt=${inputMinABV}&abv_lt=${inputMaxABV}`;
    const singleUrl = `https://api.punkapi.com/v2/beers?`;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [isError, setIsError] = useState(false);
    const { input, inputPage, inputMinABV, inputMaxABV } = useGlobalContext();

    let url = searchUrl;

    type === false ?
        !input
            ? url = searchUrl
            : url = `${searchUrl}${query}`
        : url = singleUrl;


    useEffect(() => {
        (async () => {
            setIsError(false);
            setIsLoading(false);
            try {
                console.log(url);
                const response = await axios.get(url);
                //   const totalItems = response.headers;
                setData(response.data);
                console.log(response.data);
            } catch (err) {
                setIsError(false);
            }
        })(url)
    }, [url]);
    return { isLoading, data, isError };

}

export default useFetch;
