import { ListGroup, Container, Col, Row } from 'react-bootstrap'
import { useGlobalContext } from '../context';
import ItemBeer from '../components/ItemBeer';
import { useEffect } from 'react';
const FavouriteScreen = () => {

    const {
        favouritesArray,
        setFavouritesArray,
    } = useGlobalContext();

    useEffect(() => {
        if (favouritesArray.length === 0 && localStorage.getItem('jsonOnLocalstorage')) {
            setFavouritesArray((JSON.parse(localStorage.getItem('jsonOnLocalstorage'))));
        }
    }, [])

    return (
        <Container>
            <Row>
                <Col>
                    <h1 className='text-center my-5'>Favourite</h1>
                    <ListGroup as="ul">
                        {favouritesArray.length === 0 && (<ListGroup.Item as="li" className='d-flex justify-content-between'>Nessun preferito salvato</ListGroup.Item>)}
                        {favouritesArray.filter(el => el.isFavourite === true).map((el) => <ItemBeer key={el.id} el={el} />)}
                    </ListGroup>
                </Col>
                <Col>
                    <h1 className='text-center my-5'>Deleted</h1>
                    <ListGroup as="ul">
                        {favouritesArray.filter(el => el.isFavourite === false).map((el) => <ItemBeer key={el.id} el={el} />)}
                    </ListGroup>
                </Col>
            </Row>

        </Container>
    )
}

export default FavouriteScreen;
