import { useEffect, useState } from "react";
import { ListGroup, } from 'react-bootstrap';
//import AddFavouritesButton from './AddFavouritesButton';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';

const ItemBeer = ({ el, id }) => {

    const beerName = el.name;
    //console.log(localStorage.getItem(beerName));
    let startIcon = <StarBorderIcon />;
    const [favourites, setFavourites] = useState(localStorage.getItem(beerName));

    if (favourites) {
        console.log('true', favourites)
        startIcon = <StarIcon />
    } else {
        console.log('false', favourites)
        startIcon = <StarBorderIcon />
    }


    const saveLocalstorage = () => {
        //console.log('fnLocalStorage: ', favourites);
        localStorage.setItem(beerName, !favourites);
        setFavourites(!favourites);
    }
    console.log("favourites", favourites)
    return (
        <ListGroup.Item as="li" key={id} className='d-flex justify-content-between'><div>{beerName}</div>
            <div onClick={() => saveLocalstorage()}>{startIcon}</div>
        </ListGroup.Item>

    )
}

export default ItemBeer;
