import { useState, useEffect } from "react";
import axios from 'axios';

const useFetch = (page, beerName, type = false) => {
    const customQuery = `https://api.punkapi.com/v2/beers?page=${page}&beer_name=${beerName}`;
    const allBeerQuery = `https://api.punkapi.com/v2/beers?page=${page}`;
    const url = type ? customQuery : allBeerQuery;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [count, setCount] = useState(0);
    const [isError, setIsError] = useState(false);

    useEffect(() => {
        (async (page, beerName) => {
            setIsError(false);
            setIsLoading(false);
            try {
                const response = await axios.get(`${url}`);
                setData(response.data);
                setCount(response.data);
                console.log(response.data);
            } catch (err) {
                setIsError(false);
                setCount(0);
            }
        })(page, beerName)
    }, [url, page, beerName]);
    return { isLoading, data, isError, count };

}

export default useFetch;
