import { useState } from 'react'
import { Container, Row, Col } from 'react-bootstrap';
import { useGlobalContext } from '../context';

const FilterForm = () => {

    const {
        queryBeerName,
        setQueryBeerName,
        searchBeer,
    } = useGlobalContext();
    const [input, setInput] = useState('queryBeerName');

    const handleSubmit = (e) => {
        e.preventDefault();
        searchBeer(input);
    }

    return (
        <Container>
            <form onSubmit={handleSubmit}>
                <Row>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='lg-input' type="text" placeholder="Filter by name or food" name="filterBy" autoComplete='off' onChange={(e) => setInput(e.target.value)} />
                    </Col>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='sm-input' type="number" placeholder="DA" name="numberOfPartecipants" id="numberOfPartecipants" /*onChange={(e) => setPartecipantInput(e.target.value)}*/ />
                        <input className='sm-input' type="number" placeholder="A" name="numberOfPartecipants" id="numberOfPartecipants" /*onChange={(e) => setPartecipantInput(e.target.value)}*/ />
                    </Col>
                </Row>
            </form>
        </Container>
    );
};

export default FilterForm;
