import { useState } from "react";
import { Container, ListGroup, } from 'react-bootstrap';
import AddFavouritesButton from './AddFavouritesButton';
import ReactPaginate from 'react-paginate';
import { useGlobalContext } from '../context';

const ResponsePagination = (props) => {

    const { setQueryPage } = useGlobalContext();
    const handlePageClick = (data) => {
        setQueryPage(data.selected + 1);
    }

    const [favourites, setFavourites] = useState(false);

    console.log("favourites", favourites)

    console.log("props", props.data)

    return (
        <Container className='my-3'>
            <ReactPaginate
                previousLabel={''}
                nextLabel={''}
                pageCount={3}
                onPageChange={handlePageClick}
                pageRangeDisplayed={25}
                containerClassName={'pagination d-flex justify-content-end'}
                pageClassName={'page-item'}
                pageLinkClassName={'page-link_custom'}
                activeClassName={'active_pagination'}
            />
            <ListGroup as="ul">
                {props.data.map((el) => (
                    <ListGroup.Item key={el.id} as="li" className='d-flex justify-content-between'><div>{el.name}</div>
                        <div onClick={() => setFavourites(!favourites)}><AddFavouritesButton favourites={favourites} /></div>
                    </ListGroup.Item>
                ))}
            </ListGroup>
        </Container>

    );
};

export default ResponsePagination;
