import { useNavigate } from "react-router-dom";
import { ListGroup, } from 'react-bootstrap';
import AddFavouritesStar from "./AddFavouritesStar";

const ItemBeer = ({ el, del }) => {

    let navigate = useNavigate();
    return (<>
        {del && <ListGroup.Item as="li" className='d-flex justify-content-between deleted-items' onClick={() => { navigate(`/beers/${el.id}`) }} >
            <div><h5>{el.name}</h5></div>
            <div className='d-flex'>
                <div className='px-5'>{el.abv}%</div>
                <AddFavouritesStar el={el} />
            </div>
        </ListGroup.Item >
        }
        {!del && <ListGroup.Item as="li" className='d-flex justify-content-between' onClick={() => { navigate(`/beers/${el.id}`) }} >
            <div><h5>{el.name}</h5></div>
            <div className='d-flex'>
                <div className='px-5'>{el.abv}%</div>
                <AddFavouritesStar el={el} />
            </div>
        </ListGroup.Item >
        }
    </>
    )
}
export default ItemBeer;
