import { useGlobalContext } from './context'
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import Navbar1 from './components/Navbar';
import HomeScreen from './screen/HomeScreen';
import FavouritesScreen from './screen/FavouritesScreen';
import ErrorScreen from './sreen/ErrorScreen';
import SingleBeerScreen from './screen/SingleBeerScreen';


function App() {
  const prova = useGlobalContext();
  return (
    <Router className='App'>
      <Navbar1 />
      <Switch>
        <Route path='/welcome' element={HomeScreen} />
        <Route path='/favourites' element={FavouritesScreen} />
        <Route path='beer/:id' element={SingleBeerScreen} />
        <Route path='*' element={ErrorScreen} />
      </Switch>

    </Router>
  );
}

export default App;
