import { useState } from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { useGlobalContext } from '../context';

const FilterForm = () => {

    const {
        searchBeer,
        setTypeOfSearch
    } = useGlobalContext();

    const [input, setInput] = useState('');
    //const [inputMin, setInputMin] = useState();
    //const [inputMax, setInputMax] = useState();

    const handleSubmit = (e) => {
        e.preventDefault();
        searchBeer(input);
    }

    const handleSearch = (e) => {
        setTypeOfSearch(e.target.value);
        console.log(e.target.value)
    }

    return (
        <Container>
            <form onSubmit={handleSubmit}>
                <Row>

                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <Form.Select aria-label="Default select example" onChange={(e) => handleSearch(e)} >
                            <option value="0">by Beer Name</option>
                            <option value="1">by Food</option>
                        </Form.Select>
                        <input className='lg-input' type="text" placeholder="Filter by name or food" name="filterBy" autoComplete='off' onChange={(e) => setInput(e.target.value)} />
                    </Col>
                    <Col xs={12} md={6} className='d-flex justify-content-center my-3'>
                        <input className='sm-input' type="number" placeholder="DA" name="inputMin" id="inputMin" /*onChange={(e) => setInputMin(e.target.value)}*/ />
                        <input className='sm-input' type="number" placeholder="A" name="inputMax" id="inputMax" /*onChange={(e) => setInputMax(e.target.value)}*/ />
                    </Col>
                </Row>
                <Button type='submit' >Submit</Button>
            </form>
        </Container>
    );
};

export default FilterForm;
