import { useState, useEffect } from "react";
import { ListGroup, } from 'react-bootstrap';
import AddFavouritesButton from './AddFavouritesButton';

const ItemBeer = ({ el, id }) => {

    const [favourites, setFavourites] = useState(false);

    useEffect(() => {
        localStorage.setItem("favourite beer", JSON.stringify(favourites));
    }, [favourites]);

    return (
        <ListGroup as="ul" key={id}>
            <ListGroup.Item as="li" className='d-flex justify-content-between'><div>{el.name}</div>
                <div onClick={() => setFavourites(!favourites), saveLocalstorage()}><AddFavouritesButton favourites={favourites} /></div>
            </ListGroup.Item>
        </ListGroup>
    )
}

export default ItemBeer;
