import React from 'react';
import { useParams } from 'react-router-dom';
import useFetch from '../useFetch';
import { Container, Row, } from 'react-bootstrap';

const SingleBeerScreen = () => {
    const { id } = useParams();
    const query = `/${id}`;
    const { isError, loading, data } = useFetch(`${query}`);

    const beerDetails = data;
    console.log(beerDetails);

    return (
        <Container fluid>
            <Container>
                {loading && <div>LOADING</div>}
                {isError && <div>ERROR</div>}
                {<h1>{beerDetails.name}</h1>}
                <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Reprehenderit nulla quibusdam illo eveniet consequuntur quod, impedit quo mollitia necessitatibus hic sapiente ea nemo totam voluptatem adipisci culpa a placeat suscipit.</p>
            </Container>
            <Row className='bg-yellow d-flex justify-content-center'>
                <div className='bg-yellow d-flex justify-content-center flex-wrap p-3'>
                    <div className='d-flex justify-content-center p-4'>ABV: 5.5%</div>
                    <div className='d-flex justify-content-center p-4'>SRM: 8.5%</div>
                    <div className='d-flex justify-content-center p-4'>SRM: 8.5%</div>
                    <div className='d-flex justify-content-center p-4'>IBC: 17</div>
                    <div className='d-flex justify-content-center p-4'>IBU: 17</div>
                    <div className='d-flex justify-content-center p-4'>PH: 4.4</div>
                </div>
            </Row>
            <Container>
                <h4 className='my-3'>Home</h4>
                <Row>
                    <div className='d-flex justify-content-center flex-wrap p-3'>
                        <div className='d-flex justify-content-center p-4'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum inventore maiores vitae modi numquam quam obcaecati, molestias, tenetur distinctio deserunt hic quas rem omnis sint commodi ullam nemo quaerat. Quisquam!</div>
                        <div className='d-flex justify-content-center p-4'>SRM: 8.5%</div>
                        <div className='d-flex justify-content-center p-4'>SRM: 8.5%</div>
                        <div className='d-flex justify-content-center p-4'>IBC: 17</div>
                        <div className='d-flex justify-content-center p-4'>IBU: 17</div>
                        <div className='d-flex justify-content-center p-4'>PH: 4.4</div>
                    </div>
                </Row>
            </Container>
        </Container>
    )
}

export default SingleBeerScreen;
