import { useState, useEffect } from "react";
import axios from 'axios';

const searchUrl = 'https://api.punkapi.com/v2/beers?page=';
const singleUrl = 'https://api.punkapi.com/v2/beers?page=';


const useFetch = (page, beerName, type) => {
    const url = type ? singleUrl : searchUrl;
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [count, setCount] = useState(0);
    const [isError, setIsError] = useState(false);

    useEffect(() => {
        (async () => {
            setIsError(false);
            setIsLoading(false);
            try {
                const response = await axios.get(`${url}${page}&beer_name=${beerName}`);
                setData(response.data);
                setCount(response.data);
                console.log(response.data);
            } catch (err) {
                setIsError(false);
                setCount(0);
            }
        })(page, beerName)
    }, [url, page, beerName]);
    return { isLoading, data, isError, count };

}

export default useFetch;
